import Couplings from './couplings';

import logger from './utils/logger';

logger.info('Booting Beverage Machine');

export default Couplings;

/** 
 * Trying to simulate Dependency Injection,
 * without complex DI containers
 */

import logger from './utils/logger';

import CoffeeMachine from './machine/CoffeeMachine';

import IngredientIndicator from './implementation/IngredientIndicator';

import BeverageRepository from './repository/BeverageRepository';
import IngredientRepository from './repository/IngredientRepository';

import BeverageFactory from './factory/BeverageFactory';
import IngredientFactory from './factory/IngredientFactory';

import Constants from './utils/constants';



/************** INSTANCES ******************/

const loggerInstance = logger;

const beverageFactoryInstance = new BeverageFactory(loggerInstance);
const ingredientFactoryInstance = new IngredientFactory(loggerInstance);

const beverageRepositoryInstance = new BeverageRepository(Constants.BEVERAGES_REPOSITORY, Constants.BEVERAGES_DATA_FILE_NAME, loggerInstance);
const ingredientRepositoryInstance = new IngredientRepository(Constants.INGREDIENT_REPOSITORY, Constants.INGREDIENTS_DATA_FILE_NAME, loggerInstance);

const coffeeMachineInstance = new CoffeeMachine(beverageFactoryInstance, beverageRepositoryInstance, loggerInstance);

const ingredientIndicator = new IngredientIndicator(ingredientRepositoryInstance, loggerInstance);
/*********************************************/


export default {
    BEVERAGE_MACHINE: coffeeMachineInstance,
    BEVERAGE_REPOSITORY: beverageRepositoryInstance,
    INGREDIENT_REPOSITORY: ingredientRepositoryInstance,
    GLOBAL_LOGGER: logger,
    BEVERAGE_FACTORY: beverageFactoryInstance,
    INGREDIENT_FACTORY: ingredientFactoryInstance,
    INGREDIENT_INDICATOR: ingredientIndicator,
}

import bunyan from 'bunyan';

const logger = bunyan.createLogger({
    name: `[beverage-machine]`,
    stream: process.stdout,
    level: 'trace',
});

// to disable bunyan logs in 'test' mode
if (process.env.NODE_ENV === 'test')
    logger.level(bunyan.FATAL + 1);

export default logger;

import AbstractBeverageMachine from "./AbstractBeverageMachine";


export default class CoffeeMachine extends AbstractBeverageMachine {


}

import Beverage from "../implementation/Beverage";
import { BeverageModel } from "Interfaces";
import AbstractFactory from "../factory/AbstractFactory";
import AbstractBeverageRepository from "../repository/AbstractBeverageRepository";


export default abstract class AbstractBeverageMachine {

    private logger: any;
    private beverageFactory: AbstractFactory;
    private beverageRepository: AbstractBeverageRepository;

    constructor(beverageFactory: AbstractFactory, beverageRepository: AbstractBeverageRepository, loggerInstance: any) {
        this.logger = loggerInstance;
        this.beverageFactory = beverageFactory;
        this.beverageRepository = beverageRepository;
    }

    public createBeverage(beverageModel: BeverageModel): Beverage {
        const beverage = <Beverage> this.beverageFactory.createInstance(beverageModel);
        return beverage.prepare();
    }

    public getBeverages(): Array<BeverageModel> {
        return this.beverageRepository.getAllBeverages();
    }

}

import AbstractIngredientRepository from "../repository/AbstractIngredientRepository";


export default class IngredientIndicator {

    private lowIngredients: Array<string>;
    private logger: any;
    private ingredientRepository: AbstractIngredientRepository;

    constructor(ingredientRepositoryInstance: AbstractIngredientRepository, logger: any) {
        this.logger = logger;
        this.lowIngredients = [];
        this.ingredientRepository = ingredientRepositoryInstance;
    }

    public addIngredient(ingrendientName: string) {
        const alreadyIncluded = this.lowIngredients.some(ingName => ingName === ingrendientName);
        if (!alreadyIncluded) {
            this.lowIngredients.push(ingrendientName);
            this.logger.info(`${ingrendientName} added to indicator!!`);
        }
    }

    public removeIngredient(ingrendientName: string) {
        const idx = this.lowIngredients.indexOf(ingrendientName);
        if (idx != -1) {
            this.lowIngredients.splice(idx, 1);
            this.logger.info(`${ingrendientName} removed from indicator!!`);
        }
    }

    public getAllIngredients() {
        return this.lowIngredients;
    }

    public refillLowIngredient(ingrendientName: string, quantity: number) {
        this.ingredientRepository.refillIngredient(ingrendientName, quantity);
    }

}

import AbstractBeverageRepository from "./AbstractBeverageRepository";


export default class BeverageRepository extends AbstractBeverageRepository {

}

import AbstractRepository from "./AbstractRepository";
import { BeverageModel } from "Interfaces";


export default abstract class AbstractBeverageRepository extends AbstractRepository {

    public getBeverageById(beverageId: string): BeverageModel {
        return <BeverageModel> this.get(beverageId);
    }

    public getAllBeverages(): Array<BeverageModel> {
        return <Array<BeverageModel>> this.getAll();
    }

}

import path from 'path';
import { DataModel } from 'Interfaces';


export default abstract class AbstractRepository {

    private map: Map<string, DataModel>;
    private logger: any;
    private name: string;

    constructor(repositoryName: string, repositoryFileName: string, logger: any) {
        this.name = repositoryName;
        this.logger = logger;
        this.map = new Map<string, DataModel>();
        this.prepareRepository(repositoryFileName);
    }

    protected get(key: string): DataModel {
        return this.map.get(key);
    }

    protected getAll(): Array<DataModel> {
        const result = [];
        for (const value of this.map.values())
            result.push(value);

        return result;
    }

    public put(key: string, obj: DataModel): void {
        this.map.set(key, obj);
    }

    public remove(key: string): void {
        this.map.delete(key);
    }

    private prepareRepository(configFileName: string) {
        this.logger.info(`preparing data repository for ${this.name}`);
        const configFilePath = path.resolve(path.join('config', configFileName));
        const config = require(configFilePath);
        for (const conf of config) {
            const { id } = conf;
            this.map.set(id, conf);
        }
    }

}

import AbstractIngredientRepository from "./AbstractIngredientRepository";


export default class IngredientRepository extends AbstractIngredientRepository {

}

import AbstractRepository from "./AbstractRepository";
import { IngredientModel } from "Interfaces";
import Ingredient from "../implementation/Ingredient";
import Couplings from "../couplings";


export default abstract class AbstractIngredientRepository extends AbstractRepository {


    // synchronized method
    public fetchIngredient(ingredientName: string, requiredQuantity: number): boolean {
        const ingredientModel = <IngredientModel> this.get(ingredientName);
        if (!ingredientModel)
            return false;

        const { quantity: availableQuantity } = ingredientModel;

        if (requiredQuantity <= availableQuantity) {
            ingredientModel.quantity = availableQuantity - requiredQuantity;
            this.put(ingredientName, ingredientModel);
            if (ingredientModel.quantity <= ingredientModel.low_quantity) {
                Couplings.INGREDIENT_INDICATOR.addIngredient(ingredientName);
            }

            return true;
        }

        return false;
    }

    public fillIngredient(ingredientInstance: Ingredient): void {
        const ingredientModel = <IngredientModel> this.get(ingredientInstance.getId());
        const { quantity: availableQuantity } = ingredientModel;
        const refillQuantity = ingredientInstance.getQuantity();
        ingredientModel.quantity = availableQuantity + refillQuantity;
        this.put(ingredientInstance.getId(), ingredientModel);
    }

    public refillIngredient(ingredientName: string, refillQuantity: number) {
        const ingredientModel = <IngredientModel> this.get(ingredientName);
        const { quantity: availableQuantity } = ingredientModel;
        ingredientModel.quantity = availableQuantity + refillQuantity;

        if (ingredientModel.low_quantity < ingredientModel.quantity) {
            Couplings.INGREDIENT_INDICATOR.removeIngredient(ingredientName);
        }

        this.put(ingredientName, ingredientModel);
    }

}

import Beverage from "../implementation/Beverage";
import { BeverageModel } from "Interfaces";
import AbstractFactory from "./AbstractFactory";


export default class BeverageFactory extends AbstractFactory {

    public createInstance(beverageModel: BeverageModel): Beverage {
        return new Beverage(<BeverageModel> beverageModel, this.logger);
    }

}

import { BeverageModel } from "Interfaces";
import Ingredient from "./Ingredient";
import LowIngredientException from "../exceptions/LowIngredientException";
import AbstractFactory from "../factory/AbstractFactory";

import Coupling from '../app';

export default class Beverage {

    private model: BeverageModel;

    private logger: any;
    private ingredientFactory: AbstractFactory;

    constructor(beverageModel: BeverageModel, loggerInstance: any) {
        this.model = beverageModel;
        this.logger = loggerInstance;
        this.ingredientFactory = Coupling.INGREDIENT_FACTORY;
    }

    private getIngredients(): Array<Ingredient> {
        const ingredients = [];
        for (const ingredientModel of this.model.ingredients_list) {
            const ingredientInstance = this.ingredientFactory.createInstance(ingredientModel);
            ingredients.push(ingredientInstance);
        }

        return ingredients;
    }

    private rollbackFetchedIngredients(ingredients: Array<Ingredient>): void {
        for (const ingredient of ingredients) {
            ingredient.refillIngredient();
            this.logger.info(`${ingredient.getId()} refilled!`);
        }
    }

    // template method
    public prepare(): Beverage {
        const ingredients = this.getIngredients();
        const fetchedIngredients = [];
        for (const ing of ingredients) {
            try {
                ing.fetchIngredient();
                fetchedIngredients.push(ing);
            } catch (e) {
                if (e instanceof LowIngredientException) {
                    e.printErrorMessage();
                } else {
                    this.logger.error(`${e.message}`);
                }

                this.logger.info(`refilling fetched ingredients`);
                this.rollbackFetchedIngredients(fetchedIngredients);
                return undefined;
            }
        }

        return this;
    }

}

import AbstractException from "./AbstractException";


export default class LowIngredientException extends AbstractException {


}

import Coupling from '../app';

export default abstract class AbstractException extends Error {

    protected logger: any

    constructor(message: string) {
        super(message);
        this.logger = Coupling.GLOBAL_LOGGER;

        Object.setPrototypeOf(this, AbstractException.prototype);
    }

    printErrorMessage() {
        return this.logger.error(this.message);
    }
}

import { DataModel } from "Interfaces";


export default abstract class AbstractFactory {

    protected logger: any;

    constructor(loggerInstance: any) {
        this.logger = loggerInstance;
    }

    public abstract createInstance(model: DataModel): object;

}

import AbstractFactory from "./AbstractFactory";
import Ingredient from "../implementation/Ingredient";
import { IngredientModel } from "Interfaces";


export default class IngredientFactory extends AbstractFactory {

    public createInstance(model: IngredientModel): Ingredient {
        return new Ingredient(<IngredientModel> model);
    }

}

import { IngredientModel } from "Interfaces";
import AbstractIngredientRepository from "../repository/AbstractIngredientRepository";
import Coupling from "../app";
import LowIngredientException from "../exceptions/LowIngredientException";
import IngredientIndicator from "./IngredientIndicator";


export default class Ingredient {

    private model: IngredientModel;
    private ingredientRepository: AbstractIngredientRepository;
    private ingredientIndicator: IngredientIndicator;

    constructor(ingredientModel: IngredientModel) {
        this.model = ingredientModel;
        this.ingredientRepository = Coupling.INGREDIENT_REPOSITORY;    // dependency injection
        this.ingredientIndicator = Coupling.INGREDIENT_INDICATOR;
    }

    public fetchIngredient(): boolean {
        const fetchedIngredient = this.ingredientRepository.fetchIngredient(this.getId(), this.getQuantity());
        if (!fetchedIngredient)
            throw new LowIngredientException(`${this.getId()}'s quantity is low!!`);

        return true;
    }

    public refillIngredient(): void {
        this.ingredientRepository.fillIngredient(this);
    }

    public getId() {
        return this.model.id;
    }

    public getQuantity() {
        return this.model.quantity;
    }

}

const BEVERAGES_REPOSITORY = "Beverages Repository";
const INGREDIENT_REPOSITORY = "Ingredients Repository";

const BEVERAGES_DATA_FILE_NAME = "beverages.json";
const INGREDIENTS_DATA_FILE_NAME = "ingredients.json";

export default {
    BEVERAGES_REPOSITORY,
    INGREDIENT_REPOSITORY,
    BEVERAGES_DATA_FILE_NAME,
    INGREDIENTS_DATA_FILE_NAME,
};
